" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Alcoholic hepatitis: Clinical manifestations and diagnosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/4/28744/contributors\">",
