var title_f28_4_28749="Chemo associated nephrotoxicity";
var content_f28_4_28749=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F54722&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F54722&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Chemotherapeutic agents associated with nephrotoxicity and proposed mechanisms of renal damage",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Renal toxicity (type)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prevention",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bevacizumab and orally active tyrosine kinase inhibitors targeting VEGF (axitinib, pazopanib, sorafenib, sunitinib)",
"       </td>",
"       <td>",
"        Proteinuria, nephrotic syndrome",
"       </td>",
"       <td>",
"        Renal thrombotic microangiopathy?",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Carboplatin",
"       </td>",
"       <td>",
"        <p>",
"         Rare",
"        </p>",
"        <p>",
"         Hypomagnesemia; recurrent salt wasting",
"        </p>",
"       </td>",
"       <td>",
"        Tubular injury (reversible)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cetuximab",
"       </td>",
"       <td>",
"        Hypomagnesemia",
"       </td>",
"       <td>",
"        Tubular injury",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cisplatin",
"       </td>",
"       <td>",
"        Renal failure; renal tubular acidosis; hypomagnesemia (dose related and cumulative)",
"       </td>",
"       <td>",
"        Tubulointerstitial injury",
"       </td>",
"       <td>",
"        <p>",
"         Aggressive hydration",
"        </p>",
"        <p>",
"         Forced diuresis",
"        </p>",
"        <p>",
"         Cytoprotectants (eg, amifostine)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyclophosphamide",
"       </td>",
"       <td>",
"        <p>",
"         Hyponatremia",
"        </p>",
"        <p>",
"         Hemorrhagic cystitis",
"        </p>",
"       </td>",
"       <td>",
"        Direct effect on distal tubules",
"       </td>",
"       <td>",
"        <p>",
"         Use of mesna",
"        </p>",
"        <p>",
"         Adequate hydration",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gemcitabine",
"       </td>",
"       <td>",
"        Hemolytic uremic syndrome",
"       </td>",
"       <td>",
"        Microangiopathic lesions",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ifosfamide",
"       </td>",
"       <td>",
"        Fanconi syndrome, renal tubular acidosis, nephrogenic diabetes insipidus; hemorrhagic cystitis",
"       </td>",
"       <td>",
"        Proximal tubular damage by acrolein and chloroacetic acid",
"       </td>",
"       <td>",
"        <p>",
"         Use of mesna",
"        </p>",
"        <p>",
"         Adequate hydration",
"        </p>",
"        <p>",
"         Electrolyte monitoring",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interferons",
"       </td>",
"       <td>",
"        Proteinuria; renal failure",
"       </td>",
"       <td>",
"        Minimal changes; ATN",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interleukin-2",
"       </td>",
"       <td>",
"        Prerenal azotemia",
"       </td>",
"       <td>",
"        Renal hypoperfusion (capillary leak syndrome)",
"       </td>",
"       <td>",
"        <p>",
"         Strict patient selection",
"        </p>",
"        <p>",
"         Control volume and hemodynamic status",
"        </p>",
"        <p>",
"         Avoid other nephrotoxins",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Methotrexate",
"       </td>",
"       <td>",
"        Non-oliguric renal failure (high doses)",
"       </td>",
"       <td>",
"        Intratubular deposition of methotrexate",
"       </td>",
"       <td>",
"        <p>",
"         Aggressive hydration with normal saline",
"        </p>",
"        <p>",
"         Urine alkalinization",
"        </p>",
"        <p>",
"         Forced diuresis (3 L/day)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyponatremia",
"       </td>",
"       <td>",
"        SIADH",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mitomycin C",
"       </td>",
"       <td>",
"        Hemolytic uremic syndrome (4 to 6 percent)",
"       </td>",
"       <td>",
"        Microangiopathic lesions",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nitrosureas",
"       </td>",
"       <td>",
"        Chronic interstitial nephritis with progressive renal failure",
"       </td>",
"       <td>",
"        Glomerular sclerosis tubular fibrosis",
"       </td>",
"       <td>",
"        Adequate hydration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Panitumumab",
"       </td>",
"       <td>",
"        Hypomagnesemia",
"       </td>",
"       <td>",
"        Tubular injury",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rituximab",
"       </td>",
"       <td>",
"        Acute renal failure",
"       </td>",
"       <td>",
"        Tumor lysis syndrome",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sorafenib",
"       </td>",
"       <td>",
"        Proteinuria, nephrotic syndrome",
"       </td>",
"       <td>",
"        Renal thrombotic microangiopathy?",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Streptozocin",
"       </td>",
"       <td>",
"        Proteinuria common (65 to 75 percent); tubular syndromes",
"       </td>",
"       <td>",
"        Proximal tubular damage",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sunitinib",
"       </td>",
"       <td>",
"        Proteinuria, nephrotic syndrome",
"       </td>",
"       <td>",
"        Renal thrombotic microangiopathy?",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vinca alkaloids",
"       </td>",
"       <td>",
"        Hyponatremia",
"       </td>",
"       <td>",
"        SIADH",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     VEGF: vascular endothelial growth factor; ATN: acute tubular necrosis; SIADH: syndrome of inappropriate secretion of antidiuretic hormone.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
