var title_f28_45_29404="Rx of orthostatic hypotension";
var content_f28_45_29404=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F59499&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F59499&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Management of chronic orthostatic hypotension",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Correct precipitating factors (eg,",
"hypovolemia) and withdraw offending drugs (diuretics, vasodilators,",
"tranquilizers and sedatives)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Physical measures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Raise the head of the bed by 10 to 20&ordm;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Arise slowly, in stages, from supine to seated to standing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dorsiflexion",
"of the feet, handgrip isometric or mental exercise before standing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Leg-crossing and squatting may help those",
"with autonomic failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Small meals and coffee only in the early morning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Jobst stockings and pressure suits (often not acceptable)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fludrocortisone with a high salt diet is the treatment of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sympathomimetic drugs - ephedrine,",
"phenylpropanolamine, pseudoephedrine, phenylephrine, midodrine,",
"methylphenidate, dextroamphetamine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Supplementary agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Nonsteroidal antiinflammatory drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Caffeine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Erythropoietin (if anemic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Third-line and experimental drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Vasopressin receptor agonists - dDAVP, lysine vasopressin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Clonidine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Yohimbine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Somatostatin analogues - octreotide, especially for",
"postprandial hypotension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Dihydroergotamine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Beta blockers, especially",
"those with intrinsic sympathomimetic activity such as pindolol",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Fluoxetine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Dihydroxyphenylserine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Dopamine antagonists - metoclopramide, domperidone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Monoamine oxidase inhibitor with tyramine - can produce severe hypertension",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
