var title_f28_47_29435="Classification of allergic rhinitis";
var content_f28_47_29435=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F85849&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F85849&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of allergic rhinitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        \"Intermittent\" means that the symptoms are present:",
"        <ul>",
"         <li>",
"          Less than&nbsp;four days a week",
"         </li>",
"         <li>",
"          Or for less than&nbsp;four weeks",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        \"Persistent\" means that the symptoms are present:",
"        <ul>",
"         <li>",
"          More than&nbsp;four days a week",
"         </li>",
"         <li>",
"          And for more than&nbsp;four weeks",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        \"Mild\" means that none of the following items are present:",
"        <ul>",
"         <li>",
"          Sleep disturbance",
"         </li>",
"         <li>",
"          Impairment of daily activities, leisure and/or sport",
"         </li>",
"         <li>",
"          Impairment of school or work",
"         </li>",
"         <li>",
"          Troublesome symptoms",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        \"Moderate-severe\" means that one or more of the following items are present:",
"        <ul>",
"         <li>",
"          Sleep disturbance",
"         </li>",
"         <li>",
"          Impairment of daily activities, leisure and/or sport",
"         </li>",
"         <li>",
"          Impairment of school or work",
"         </li>",
"         <li>",
"          Troublesome symptoms",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Bousquet J, Van Cauwenberge P, Khaltaev N, et al. Allergic rhinitis and its impact on asthma. J Allergy Clin Immunol 2001; 108:S147. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
