var title_f28_49_29468="Healthy People 2020 nutrition objectives";
var content_f28_49_29468=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F57047&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F57047&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Healthy People 2020: Selected nutrition objectives",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Weight status",
"       </td>",
"       <td class=\"subtitle1_single\">",
"        2020 targets*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce the proportion of children 2 to 5 years who are obese",
"       </td>",
"       <td>",
"        &nbsp;9.6 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce the proportion of&nbsp;children 6 to 11 years&nbsp;who are obese",
"       </td>",
"       <td>",
"        &nbsp;15.7 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce the proportion of adolescents who are obese",
"       </td>",
"       <td>",
"        &nbsp;16.1 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce the proportion of adults who are obese",
"       </td>",
"       <td>",
"        &nbsp;30.6 percent&nbsp;&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Food and nutrient consumption (2 years and older)",
"       </td>",
"       <td class=\"subtitle1_single\">",
"        2020 targets",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increase the contribution of fruits to the diet&nbsp;&nbsp;",
"       </td>",
"       <td>",
"        0.9 cup-equivalents per 1000 calories",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increase the contribution of vegetables to the diet, with at least one-third of these servings being dark green or deep yellow vegetables",
"       </td>",
"       <td>",
"        1.1 cup-equivalents total vegetables per 1000 calories",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increase the contribution of&nbsp;whole grains&nbsp;to the diet&nbsp;",
"       </td>",
"       <td>",
"        0.6 ounce-equivalents of whole grains per 1000 calories",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce the consumption of calories from solid fats and saturated fat",
"       </td>",
"       <td>",
"        <p>",
"         Solid fats no more than 16.7 percent of caloric intake",
"        </p>",
"        <p>",
"         Saturated fats no more than 9.5 percent of caloric intake",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce the consumption of calories from added sugars",
"       </td>",
"       <td>",
"        Added sugars no more than 10.8 percent of caloric intake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increase consumption of calcium",
"       </td>",
"       <td>",
"        1300 milligrams daily&nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Target selected to represent a 10 percent improvement over baseline. In children and adolescents, obesity is defined as &ge;95th percentile of body mass index (BMI) for age and gender. In adults, obesity is defined as&nbsp;BMI &ge;30.",
"     <br>",
"      &bull; Target selected to represent an achievable shift in the population intake, based on past trends and consideration of&nbsp;the target's&nbsp;applicability to subpopulations.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     From US DHHS. Healthy People 2020: Nutrition and weight status objectives. Available at:",
"     <a href=\"file://www.healthypeople.gov/2020/topicsobjectives2020/objectiveslist.aspx?topicId=29\">",
"      file://www.healthypeople.gov/2020/topicsobjectives2020/objectiveslist.aspx?topicId=29",
"     </a>",
"     (accessed 3/27/2012)",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
