" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Zenker's diverticulum",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/52/29512/contributors\">",
