var title_f28_55_29563="Criteria for panic attack";
var content_f28_55_29563=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F79985&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F79985&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for panic attack",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        A discrete period of intense fear or discomfort, in which four (or more) of the following symptoms developed abruptly and reach a peak within 10 minutes:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. Palpitations, pounding heart, or accelerated heart rate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2. Sweating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3. Trembling or shaking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4. Sensations of shortness of breath or smothering",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        5. Feeling of choking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        6. Chest pain or discomfort",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        7. Nausea or abdominal distress",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        8. Feeling dizzy, unsteady, lightheaded, or faint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        9. Derealization (feelings of unreality) or depersonalization (being detached from oneself)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        10. Fear of losing control or going crazy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        11. Fear of dying",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        12. Paresthesias (numbness or tingling sensations)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        13. Chills or hot flushes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
