var title_f28_55_29564="Ambulatory detox protocols";
var content_f28_55_29564=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F66121&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F66121&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ambulatory detoxification protocols",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Reduce moderate withdrawal symptoms and risk for serious withdrawal symptoms - fixed dosing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        If withdrawal symptoms are moderate (ie, CIWA score between 8 and 15) then use one of the following protocols:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Long-acting benzodiazepine protocol - (Chlordiazepoxide)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 1 - 50 mg every 6 to 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 2 - 25 mg every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 3 - 25 mg twice a day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 4 - 25 mg at night",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Shorter-acting benzodiazepine protocol (Oxazepam)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 1 - 30 mg every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 2 - 30 mg every 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 3 - 30 mg every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 4 - 30 mg at night",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anti-convulsant Protocol (Carbamazepine)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 1 - 200 mg every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 2 - 200 mg every 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 3 - 200 mg every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 4 - 200 mg one dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Reduce or prevent mild withdrawal symptoms - symptom triggered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        If withdrawal symptoms are not present or mild (ie, CIWA score less than 8) then use one of the following protocols:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Long-acting benzodiazepine protocol - (Chlordiazepoxide)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 1 - 50 mg every 6 to 12 hours as needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Days 2 to 5 - 25 mg every 6 hours as needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Shorter-acting benzodiazepine protocol (Oxazepam)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Day 1 - 30 mg every 6 hours as needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Days 2 to 5 - 15 mg every 6 hours as needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Nutritional support",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Thiamine 100 mg for 3 days, multivitamins maintenance",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
