var title_f28_56_29580="Characteristics US postacute rehab settings";
var content_f28_56_29580=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F86677&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F86677&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristics of US postacute care settings where rehabilitation may be provided",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"6\" width=\"16%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Rehabilitation site",
"       </td>",
"       <td class=\"subtitle1\">",
"        Facility characteristics",
"       </td>",
"       <td class=\"subtitle1\">",
"        Medical coverage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Therapy services",
"       </td>",
"       <td class=\"subtitle1\">",
"        Nursing",
"       </td>",
"       <td class=\"subtitle1\">",
"        Insurance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inpatient rehabilitation (acute)",
"       </td>",
"       <td>",
"        Multidisciplinary team patient care conferences required within four days of admission and weekly thereafter",
"       </td>",
"       <td>",
"        Physician available 24 hours/day, 7 days/week; active medical problem(s) requiring physician supervision with visit five or more days/week",
"       </td>",
"       <td>",
"        PT, OT, and speech therapy available. Patients must need two or more therapy services and must receive therapy three or more hours per day, five days per week.",
"       </td>",
"       <td>",
"        24-hour care",
"       </td>",
"       <td>",
"        Medicare Part A: days 1 to 20: 100 percent; days 21 to 100: 80 percent plus co-payment; &gt;100 days: no coverage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Skilled nursing facility (subacute/transitional care unit)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Physician supervised; physician evaluation within two weeks of admission and every 30 days; physician available in emergencies",
"       </td>",
"       <td>",
"        PT and OT available; typically one or more session per day",
"       </td>",
"       <td>",
"        24-hour care",
"       </td>",
"       <td>",
"        Same as inpatient rehabilitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Long-term acute care hospital (LTACH)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Physician available 24 hours/day; active/ongoing medical condition requiring physician-level care (eg, ventilator dependent)",
"       </td>",
"       <td>",
"        PT and OT available",
"       </td>",
"       <td>",
"        24-hour care",
"       </td>",
"       <td>",
"        Same as inpatient rehabilitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Home therapy/nursing",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Physician referral and recertification every 60 days",
"       </td>",
"       <td>",
"        PT, OT, and speech therapy available",
"       </td>",
"       <td>",
"        Home health nursing",
"       </td>",
"       <td>",
"        Typically one to three visits per week by OT/PT for one to three weeks, one visit per week with RN, daily home health aid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Outpatient (hospital or free-standing clinic)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Physician referral and recertification every 30 days",
"       </td>",
"       <td>",
"        PT, OT, and speech therapy available",
"       </td>",
"       <td>",
"        N/A",
"       </td>",
"       <td>",
"        May be limitations in number of visits per year",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     OT: occupational therapy; PT: physical therapy.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Geriatrics Review Syllabus: A Core Curriculum in Geriatric Medicine, 7th ed, Pacala JT, Sullivan GM (Eds), American Geriatrics Society, New York, 2010.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f28_56_29580=[""].join("\n");
