"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/57/29594/contributors\">",
"     Rebecca F Connor, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?28/57/29594/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
