var content_f28_57_29596=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F59019&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F59019&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Management of hypocalcemia after thyroid surgery",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"25%\">",
"     </colgroup>",
"     <colgroup width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Postoperative day",
"       </td>",
"       <td class=\"subtitle1\">",
"        Serum tests",
"       </td>",
"       <td class=\"subtitle1\">",
"        Therapy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Night of surgery",
"       </td>",
"       <td>",
"        Calcium in the evening (approximately 8 PM)",
"       </td>",
"       <td>",
"        <strong>",
"         Ca &lt;7.5 mg/dL:",
"        </strong>",
"        Calcitriol 0.5 mcg three times daily x three days and calcium gluconate 3 g/L D5 1/2 normal saline IV at 100 mL/hour",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Day 1",
"       </td>",
"       <td>",
"        Calcium and phosphorus in the morning (approximately 6 AM); if Ca &lt;7.5 mg/dL, add magnesium",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Ca &lt;7.5 mg/dL:",
"         </strong>",
"         Calcitriol 0.5 mcg twice daily x three days and adjust depending upon calcium response and calcium gluconate 3 g/L D5 1/2 normal saline IV at 100 mL/hour and calcium carbonate (1 to 4 g elemental calcium) by mouth daily in divided doses depending upon calcium response",
"        </p>",
"        <p>",
"         <strong>",
"          Ca 7.5 to 8.0 mg/dL:",
"         </strong>",
"         Calcitriol 0.5 mcg twice daily x three days and adjust depending upon calcium response and calcium carbonate (1 to 4 g elemental calcium) by mouth daily in divided doses depending upon calcium response",
"        </p>",
"        <p>",
"         <strong>",
"          Ca &gt;8.0 mg/dL:",
"         </strong>",
"         Calcium carbonate (1 g elemental calcium) by mouth twice daily",
"        </p>",
"        <strong>",
"         Mg &lt;2 mg/dL:",
"        </strong>",
"        Magnesium sulfate 4 g in 100 mL normal saline IV at 33 mL/hour and magnesium oxide 400 mg by mouth twice daily x one month",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Day 2 to 4",
"       </td>",
"       <td>",
"        If day 1 Ca &le;8.0 mg/dL, total calcium and phosphorus",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Ca &lt;7.5 mg/dL and symptomatic:",
"         </strong>",
"         Calcitriol 0.25 mcg three times daily and calcium gluconate 3 g/L D5 1/2 normal saline IV at 100 mL/hour and calcium carbonate (1 to 4 g elemental calcium) by mouth daily in divided doses and modify based upon calcium response",
"        </p>",
"        <p>",
"         <strong>",
"          Ca &lt;7.5 mg/dL and asymptomatic:",
"         </strong>",
"         Calcitriol 0.25 mcg three times daily and calcium carbonate (1 to 4 g elemental calcium) by mouth daily in divided doses and modify based upon calcium response",
"        </p>",
"        <p>",
"         <strong>",
"          Ca 7.5 to 8.4 mg/dL or P &ge;4.5 mg/dL:",
"         </strong>",
"         Calcitriol 0.25 mcg daily and calcium carbonate (1 to 4 g elemental calcium) by mouth daily in divided doses and modify based upon calcium response",
"        </p>",
"        <p>",
"         <strong>",
"          Ca 8.5 to 9.4 mg/dL and P &le;4.5 mg/dL:",
"         </strong>",
"         Calcium carbonate (1 to 4 g elemental calcium) by mouth daily in divided doses and modify based upon calcium response",
"        </p>",
"        <strong>",
"         Ca &ge;9.5 mg/dL:",
"        </strong>",
"        No therapy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    To convert serum calcium to mmol/L, divide by 4. To convert serum phosphorus to mmol/L, divide by 3.1.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
