var title_f28_59_29629="Pulm drug complication I";
var content_f28_59_29629=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F54967%7EPULM%2F69778&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=15\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F54967%7EPULM%2F69778&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=15\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pulmonary complications of cardiovascular drugs",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency",
"       </td>",
"       <td class=\"subtitle1\">",
"        Potential mechanism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Procainamide",
"       </td>",
"       <td>",
"        SLE-like syndrome",
"       </td>",
"       <td>",
"        50 to 90 percent of patients have positive ANA test; 10",
"to 20 percent become symptomatic; pleuritis, pleural effusion, and",
"parenchymal infiltrates are common",
"       </td>",
"       <td>",
"        Autoantibodies against histone H2A-H2B complex,",
"potentially induced by interaction of drug",
"metabolites with DNA, or drug-induced alteration in T-cell DNA",
"methylation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Quinidine",
"       </td>",
"       <td>",
"        SLE-like syndrome",
"       </td>",
"       <td>",
"        Rare; pleuritis and pleural effusions are most common",
"pulmonary signs; parenchymal infiltrates uncommon",
"       </td>",
"       <td>",
"        Autoantibodies against histone H2A-H2B complex,",
"potentially induced by interaction of drug",
"metabolites with DNA, or drug-induced alteration in T-cell DNA",
"methylation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tocainide",
"       </td>",
"       <td>",
"        Pneumonitis/fibrosis",
"       </td>",
"       <td>",
"        Incidence is about 0.3 percent",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Flecainide",
"       </td>",
"       <td>",
"        Pneumonitis/fibrosis",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mexiletine",
"       </td>",
"       <td>",
"        Pneumonitis/fibrosis",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Unknown; interferes with theophylline metabolism and",
"can cause toxic elevations",
"in theophylline levels in serum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        ACE inhibitors",
"       </td>",
"       <td>",
"        Cough",
"       </td>",
"       <td>",
"        Incidence is 5 to 15 percent; occurs with all ACE",
"inhibitors; whether these agents precipitate bronchospasm in patients",
"with asthma is controversial",
"       </td>",
"       <td>",
"        Decreased degradation of irritant and",
"bronchoconstrictive mediators (bradykinin and",
"substance P); direct increase in sensitivity to irritant-induced",
"cough; genetic predisposition (based on differences in ACE gene).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Angioneurotic edema",
"       </td>",
"       <td>",
"        Rare; can lead to respiratory failure",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sotalol",
"       </td>",
"       <td>",
"        Bronchospasm",
"       </td>",
"       <td>",
"        Occurs in 2 percent of patients",
"       </td>",
"       <td>",
"        Direct blockage of &beta;2-receptors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adenosine",
"       </td>",
"       <td>",
"        Bronchospasm",
"       </td>",
"       <td>",
"        Occurs with rapid infusion in 5 to 10 percent of",
"patients; may trigger acute bronchospasm in patients with asthma or",
"COPD. Reversible with aminophylline",
"       </td>",
"       <td>",
"        Induction of mast cell mediator release",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Redrawn from Zitnik, RJ, J Respir Dis 1996; 17:254; and Zitnik, RJ, J Respir Dis 1996; 17:293.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pulmonary complications of cardiovascular drugs",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency",
"       </td>",
"       <td class=\"subtitle1\">",
"        Potential mechanism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Beta blockers",
"       </td>",
"       <td>",
"        Cough or bronchospasm",
"       </td>",
"       <td>",
"        Common; usually occurs in patients with asthma or COPD;&nbsp;&beta;1-selective agents, labetalol, esmolol, and pindolol are",
"better tolerated than nonselective agents",
"       </td>",
"       <td>",
"        Direct antagonism of &beta;2 receptor-mediated",
"bronchodilation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary fibrosis",
"       </td>",
"       <td>",
"        Rare&nbsp;",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lupus-like syndrome",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hydralazine",
"       </td>",
"       <td>",
"        Lupus-like syndrome",
"       </td>",
"       <td>",
"        Renal disease more common than in other drug-induced",
"lupus-like syndromes",
"       </td>",
"       <td>",
"        Unknown; slow acetylation may be a risk factor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dipyridamole (IV form only)",
"       </td>",
"       <td>",
"        Bronchospasm",
"       </td>",
"       <td>",
"        Occurs in about 0.15 percent of patients during",
"infusion for stress testing",
"       </td>",
"       <td>",
"        Induction of mast cell mediator release",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Protamine",
"       </td>",
"       <td>",
"        Anaphylaxis, uritcaria, angioedema, bronchospasm",
"       </td>",
"       <td>",
"        Occurs in 0.2 percent of patients",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary hypertension",
"       </td>",
"       <td>",
"        Mild pulmonary hypertension and systemic hypotension in",
"up to 5 percent of patients; severe reaction rare",
"       </td>",
"       <td>",
"        Antiprotamine antibodies; activated neutrophils;",
"thromboxane A2 generation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Redrawn from Zitnik, RJ, J Respir Dis 1996; 17:254; and Zitnik, RJ, J Respir Dis 1996; 17:293.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
