" <!-- TC:TOPIC_PAGE -->",
" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Perioperative management of diabetes mellitus",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
