" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Supranuclear disorders of gaze in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/8/28806/contributors\">",
