var title_f29_11_29883="Marshall scale for TBI";
var content_f29_11_29883=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F67172&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F67172&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Marshall CT (computed tomography) classification of traumatic brain injury",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diffuse injury I (no visible pathology)",
"       </td>",
"       <td>",
"        No visible intracranial pathology seen on CT scan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diffuse injury II",
"       </td>",
"       <td>",
"        Cisterns are present with midline shift of 0-5 mm and/or lesions densities present; no high or mixed density lesion &gt;25 cm",
"        <sup>",
"         3",
"        </sup>",
"        may include bone fragments and foreign bodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diffuse injury III (swelling)",
"       </td>",
"       <td>",
"        Cisterns compressed or absent with midline shift 0-5 mm; no high or mixed density lesion &gt;25 cm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diffuse injury IV (shift)",
"       </td>",
"       <td>",
"        Midline shift &gt;5 mm; no high or mixed density lesion &gt;25 cm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evacuated mass lesion V",
"       </td>",
"       <td>",
"        Any lesion surgically evacuated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Non-evacuated mass lesion VI",
"       </td>",
"       <td>",
"        High or mixed density lesion &gt;25 cm",
"        <sup>",
"         3",
"        </sup>",
"        ; not surgically evacuated",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Adelson PD, Bratton SL, Carney NA, et al. Guidelines for the acute medical management of severe traumatic brain injury in infants, children, and adolescents. Chapter 1: Introduction. Pediatr Crit Care Med 2003; 4:S2. Copyright &copy; 2003 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
