var title_f29_12_29901="FTT pathophysiologic causes";
var content_f29_12_29901=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F69846&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F69846&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of failure to thrive, according to pathophysiologic mechanism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Inadequate nutrient intake",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Inappropriate feeding technique",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Disturbed caregiver/child relationship",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Economic deprivation",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Inappropriate nutrient intake (eg, excess fruit juice consumption, factitious food allergy, inappropriate preparation of formula, inadequate quantity of food, inappropriate food for age, neglect, food fads)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Inappropriate parental knowledge of appropriate diet for infants and toddlers",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Insufficient lactation in mother",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Gastroesophageal reflux",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Psychosocial problems",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Maternal/infant dysfunction",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Mechanical problems (cleft palate, nasal obstruction, adenoidal hypertrophy, dental lesions)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Sucking or swallowing dysfunction (CNS, neuromuscular, esophageal motility problems)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Inadequate appetite or inability to eat large amounts",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Psychosocial problems &mdash; apathy or rumination",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Cardiopulmonary disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Hypotonia, muscle weakness, or hypertonia",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Anorexia of chronic infection or immune deficiency",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Cerebral palsy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            CNS pathology (eg, tumor, hydrocephalus)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Genetic syndromes",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Anemia (eg, iron deficiency)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Chronic constipation",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Gastrointestinal disorder (eg, pain from gastroesophageal reflux, intestinal tract obstruction)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Craniofacial anomalies (eg, cleft lip and palate, micrognathia)",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Inadequate nutrient absorption or increased losses",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Malabsorption (lactose intolerance, cystic fibrosis, cardiac disease, malrotation, inflammatory bowel disease, milk allergy, parasites, celiac disease)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Biliary atresia, cirrhosis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Vomiting or \"spitting up\" (related to infectious gastroenteritis, increased intracranial pressure, adrenal insufficiency, or drugs [eg, purposeful administration of syrup of ipecac])",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Intestinal tract obstruction (pyloric stenosis, hernia, malrotation, intussusception)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Infectious diarrhea",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Necrotizing enterocolitis or short bowel syndrome",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Increased nutrient requirements or ineffective utilization",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Hyperthyroidism",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Malignancy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Chronic inflammatory bowel disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Chronic systemic disease (juvenile idiopathic arthritis)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Chronic or recurrent systemic infection (urinary tract infection, tuberculosis, toxoplasmosis)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Chronic metabolic problems (hypercalcemia, storage diseases, and inborn errors of metabolism, such as galactosemia, methylmalonic acidemia, diabetes mellitus, adrenal insufficiency)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Chronic respiratory insufficiency (bronchopulmonary dysplasia, cystic fibrosis)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Congenital or acquired heart disease",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
