" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Optic neuritis: Prognosis and treatment",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/15/29944/contributors\">",
"     Authors",
"    </a>",
