" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Chronic urticaria: Clinical manifestations, diagnosis, pathogenesis, and natural history",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/19/30010/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/19/30010/contributors\">",
"     Sarbjit Saini, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/19/30010/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/19/30010/contributors\">",
"     Bruce S Bochner, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/19/30010/contributors\">",
"     Jeffrey Callen, MD, FACP, FAAD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/19/30010/contributors\">",
"     Deputy Editor",
"    </a>",
