var content_f29_19_30012=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F64312&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F64312&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison of conjugate vaccines against",
"    <em>",
"     Haemophilus influenzae",
"    </em>",
"    type b licensed in the United States",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Vaccine",
"        <br/>",
"        (commercial name)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Carrier protein",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommended age of administration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Monovalent Hib vaccines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PRP-OMP*",
"        <br/>",
"        (PedVaxHIB)",
"       </td>",
"       <td>",
"        Outer membrane protein complex of",
"        <em>",
"         Neisseria meningitidis",
"        </em>",
"       </td>",
"       <td>",
"        2, 4, and 12 through 15 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PRP-T",
"        <br/>",
"        (ActHIB)",
"       </td>",
"       <td>",
"        Tetanus toxoid",
"       </td>",
"       <td>",
"        2, 4, 6, and 12 through 15 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PRP-T",
"        <br/>",
"        (Hiberix)",
"       </td>",
"       <td>",
"        Tetanus toxoid",
"       </td>",
"       <td>",
"        12 through 15 months (following primary series with PRP-OMP or ActHIB)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Combination Hib vaccines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PRP-OMP-Hepatitis B*",
"        <br/>",
"        (Comvax)",
"       </td>",
"       <td>",
"        Outer membrane protein complex of",
"        <em>",
"         N. meningitidis",
"        </em>",
"       </td>",
"       <td>",
"        2, 4, and 12 through 15 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        DTaP-IPV/PRP-T",
"        <br/>",
"        (Pentacel)",
"       </td>",
"       <td>",
"        Tetanus toxoid",
"       </td>",
"       <td>",
"        2, 4, 6, and 15 through 18 months",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        HibMenCY",
"        <sup>",
"         &Delta;",
"        </sup>",
"        <br/>",
"        (Menhibrix)",
"       </td>",
"       <td>",
"        Tetanus toxoid",
"       </td>",
"       <td>",
"        2, 4, 6, and 12 through 15 months",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PRP-OMP: polyribosylribitol phosphate conjugated to outer membrane protein complex of",
"     <em>",
"      N. meningitidis",
"     </em>",
"     ; PRP-T: polyribosylribitol phosphate conjugated to tetanus toxoid; DTaP: diphtheria toxoid-tetanus toxoid-acellular pertussis vaccine; IPV: inactivated polio vaccine; HibMenCY: combination",
"     <em>",
"      Haemophilus influenzae",
"     </em>",
"     type b and meningococcus serogroups C and Y conjugate vaccine.",
"     <br/>",
"     * Preferred for Native Americans and Alaskan natives.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     May be given as early as age 12 months of age, provided at least 6 months have elapsed since the third dose of DTaP.",
"     <br/>",
"     &Delta; Recommended for use in infants at increased risk for meningococcal disease (ie, those with persistent complement pathway deficiencies or anatomic or functional asplenia [including sickle cell disease]).",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
