" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Driving restrictions in patients with an implantable cardioverter-defibrillator",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/20/30024/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/20/30024/contributors\">",
"     Tatjana N Sljapic, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/20/30024/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/20/30024/contributors\">",
"     Brian Olshansky, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/20/30024/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/20/30024/contributors\">",
"     Brian C Downey, MD, FACC",
"    </a>",
"    <br/>",
"   </div>",
