var title_f29_21_30043="Autopsy findings in SIDS";
var content_f29_21_30043=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F66184&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F66184&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Typical morphologic findings in a SIDS autopsy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        External examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Well-developed, well-nourished baby",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Frothy blood-tinged fluid around the nose (50 percent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyanosis of the lips and nail beds",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypostatic staining anteriorly suggesting face down position",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Internal examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary congestion (89 versus 80 percent in controls)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary edema (63 versus 51 percent in controls)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thymic petechiae (44 versus 25 percent in controls)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent hepatic erythropoiesis (23 versus 14 percent in controls)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        \"Subacute\" inflammation of the upper respiratory tract",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Focal fibrinoid necrosis of the larynx",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Full expansion of the lungs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Liquid blood in the heart",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal prominent lymphoid tissue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Empty urinary bladder (50 percent)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
