var title_f29_22_30059="Risk classific exercise class A";
var content_f29_22_30059=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F50753&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F50753&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk classification for exercise training: class A: apparently healthy individuals",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        This classification includes:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Children, adolescents, men &lt;45 years, and women &lt;55 years who have no symptoms or known presence of heart disease or major coronary risk factors.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Men &ge;45 years and women &ge;55 years who have no symptoms or known presence of heart disease and with &lt;2 major cardiovascular risk factors.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Men &ge;45 years and women &ge;55 years who have no symptoms or known presence of heart disease and with &ge;2 major cardiovascular risk factors.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Activity guidelines:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No restrictions other than basic guidelines.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Supervision required",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        ECG and blood pressure monitoring:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Not required",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * It is suggested that persons classified as Class A-2 and particularly Class A-3 undergo a medical examination and possibly a medically supervised exercise test before engaging in vigorous exercise.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Fletcher GF, Balady GJ, Amsterdam EA, et al. Circulation 2001; 104:1694. Copyright &copy; 2001 Lippincott Williams and Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
