var title_f29_22_30060="Geriatric depression scale";
var content_f29_22_30060=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F72822&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F72822&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Yesavage geriatric depression scale (short form)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       1. Are you basically satisfied with your life?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Have you dropped many of your activities and interests?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Do you feel that your life is empty?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Do you often get bored?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Are you in good spirits most of the time?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. Are you afraid that something bad is going to happen to you?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Do you feel happy most of the time?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       8. Do you often feel helpless?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       9. Do you prefer to stay at home, rather than going out and doing new things?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       10. Do you feel you have more problems with memory than most?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       11. Do you think it is wonderful to be alive now?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       12. Do you feel pretty worthless the way you are now?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       13. Do you feel full of energy?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       14. Do you feel that your situation is hopeless?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       15. Do you think that most people are better off than you are?",
"      </td>",
"      <td>",
"       Y",
"      </td>",
"      <td>",
"       N",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_left\" colspan=\"3\">",
"       Score 1 point for each \"depressed\" answer (no on 1, 5, 7, 11, 13; yes on others)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"2\">",
"       Normal",
"      </td>",
"      <td>",
"       3&plusmn;2",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"2\">",
"       Mildly depressed",
"      </td>",
"      <td>",
"       7&plusmn;3",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"2\">",
"       Very depressed",
"      </td>",
"      <td>",
"       12&plusmn;2",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Sheikh, JI, Yesavage, JA, Brooks, JO 3d, et al. Int Psychogeriatr 1991; 3:23.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
