" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Endoscopic ultrasound-guided celiac plexus and ganglia interventions",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/24/30088/contributors\">",
"     Authors",
"    </a>",
