var title_f29_24_30091="Eligibility criteria allo-HCT";
var content_f29_24_30091=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F88755&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F88755&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    General eligibility criteria for allogeneic hematopoietic cell transplantation (allo-HCT)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Disease-related indication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suitable donor available",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Karnofsky performance status &ge;70 for a myeloablative HCT or &ge;50 for a nonmyeloablative HCT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal function:",
"        <br/>",
"        <ul>",
"         <li>",
"          While no specific cutoff is universally used, many centers limit allo-HCT&nbsp;to those with a serum creatinine &lt;2 mg/dL (177 micromol/L) or CrCl &gt;50",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiac reserve:",
"        <br/>",
"        <ul>",
"         <li>",
"          LVEF &gt;35",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary function:",
"        <br/>",
"        <ul>",
"         <li>",
"          A corrected DLCO &gt;35 percent",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Liver function:",
"        <br/>",
"        <ul>",
"         <li>",
"          No frank cirrhosis of the liver",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adequate psychosocial and financial support",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        While no specific age cutoff is universally used, many centers limit myeloablative allo-HCT to patients &lt;55 years and reduced intensity allo-HCT to patients &lt;75 years",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     LVEF: left ventricular ejection fraction; DLCO: diffusing capacity of the lungs for carbon monoxide.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
