var title_f29_27_30140="NHL treatment response criteria";
var content_f29_27_30140=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F64921&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F64921&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    International Workshop Criteria (IWC) for assessing response to treatment in non-Hodgkin lymphoma (NHL)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Complete remission (CR)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No clinical evidence of disease or disease-related symptoms.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         &bull; Typically FDG-avid lymphomas: a post-treatment residual mass of any size is permitted as long as it is PET negative.",
"        </p>",
"        <p>",
"         &bull; Variably FDG-avid lymphoma/FDG avidity unknown: all lymph nodes normal size by CT.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Spleen and liver non-palpable and without nodules.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If pretreatment bone marrow biopsy was positive, repeat bone marrow biopsy must be negative; if morphologically indeterminate, immunohistochemistry should be negative.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Partial remission (PR)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Regression of measurable disease with at least 50 percent decrease in nodal size as determined by SPD.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For a typically FDG-avid lymphoma, the post-treatment PET should be positive in at least one previously involved site.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No increase in the size of other nodes, liver, or spleen.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Regression of splenic or hepatic nodules by at least 50 percent as determined by the SPD.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No new sites of disease.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Stable disease (SD)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Failure to attain CR/PR or PD.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For typically FDG-avid lymphomas, the post-treatment PET should be positive at prior sites of disease and no new sites should be present on PET or CT.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Relapse after CR or Progressive disease (PD)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Appearance of any new lesion more than 1.5 cm in long axis. If long axis is 1.1 to 1.5 cm, it should only be considered abnormal if its short axis is more than 1.0 cm.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        50 percent increase in the longest diameter of a previously identified node more than 1 cm in short axis or in the SPD of more than one node.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lesions &gt;1.5 cm should be PET positive in typical FDG-avid lymphoma or if PET positive before therapy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increasing FDG uptake in a previously unaffected site should only be considered relapse or PD after confirmation with other modalities.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        New or recurrent involvement of the bone marrow.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Typically FDG-avid lymphomas include: diffuse large B cell lymphoma, Hodgkin lymphoma, follicular lymphoma, and mantle cell lymphoma. Variably FDG-avid lymphomas include all other non-Hodgkin lymphomas.",
"    <div class=\"footnotes\">",
"     SPD: sum of the product of the diameters.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Cheson BD, Pfistner B, Juweid ME, et al. Revised criteria for malignant lymphoma. J Clin Oncol 2007; 25:579.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
