var title_f29_3_29755="Dealing with lead paint PI";
var content_f29_3_29755=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F66646&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F66646&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ways to prevent exposure to lead in paint",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Clean up loose paint chips right away",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wipe off loose paint using damp cloths or rags that you throw away after using",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Block access to chipping paint with furniture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Put contact paper over chipping paint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hose off the porch or put down a blanket or rug where children play",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Seal or block off areas with small amounts of chipping paint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Do NOT use unsafe ways of removing paint:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Do NOT sand off paint without a HEPA attachment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Do NOT burn off paint with an open flame",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Do NOT use chemicals to remove paint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        When hiring contractors to remove lead paint, use only people who are \"certified abatement contractors\"",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Make sure family and belongings (such as cushioned furniture) are protected during lead removal (called \"abatement\")",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Environmental Protection Agency. Additional information available at:",
"     <a href=\"file://www.epa.gov/lead/\" target=\"_blank\">",
"      file://www.epa.gov/lead/",
"     </a>",
"     .",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
