"         <span class=\"medCalcFontIO\">",
"          Input:",
"         </span>",
"         <br/>",
"         &nbsp;",
"         <br/>",
"         <table cellpadding=\"3\" cellspacing=\"0\" summary=\"MedCalc 3000 Table\">",
"          <tr>",
"           <td align=\"right\" width=\"42%\">",
"            <span class=\"medCalcFontOneBold\">",
"             Age",
"            </span>",
"           </td>",
"           <td align=\"left\" nowrap=\"nowrap\" valign=\"top\" width=\"5%\">",
"            &nbsp;",
"            <input name=\"Age_param\" onblur=\"Sokal_fx(); minMaxCheck();\" onchange=\"Sokal_fx();\" size=\"6\" type=\"text\" value=\"\"/>",
"           </td>",
"           <td align=\"left\" valign=\"top\" width=\"115\">",
"            <span style=\"white-space: nowrap;\">",
"             <span class=\"medCalcFontOneBold\">",
"              years old",
"             </span>",
"            </span>",
"           </td>",
"          </tr>",
"          <tr>",
"           <td align=\"right\" width=\"42%\">",
"            <span class=\"medCalcFontOneBold\">",
"             Spleen Below Coastal Margin",
"            </span>",
"           </td>",
"           <td align=\"left\" nowrap=\"nowrap\" valign=\"top\" width=\"5%\">",
"            &nbsp;",
"            <input name=\"Spleen_Below_Coastal_Margin_param\" onblur=\"Sokal_fx(); minMaxCheck();\" onchange=\"Sokal_fx();\" size=\"6\" type=\"text\" value=\"\"/>",
"           </td>",
"           <td align=\"left\" valign=\"top\" width=\"115\">",
"            <select class=\"medCalcFontSelect\" name=\"Spleen_Below_Coastal_Margin_unit\" onchange=\"Sokal_fx();\" style=\"width:105px;\">",
"             <option selected=\"selected\" value=\"1|0|cm\">",
