"            &nbsp;",
"            <input name=\"Blast_Percent_param\" onblur=\"Sokal_fx(); minMaxCheck();\" onchange=\"Sokal_fx();\" size=\"6\" type=\"text\" value=\"\"/>",
"           </td>",
"           <td align=\"left\" valign=\"top\" width=\"115\">",
"            <span style=\"white-space: nowrap;\">",
"             <span class=\"medCalcFontOneBold\">",
"              percent",
"             </span>",
"            </span>",
"           </td>",
"          </tr>",
"         </table>",
"        </center>",
"       </td>",
"       <td align=\"left\" bgcolor=\"#eeeeee\">",
"        <center>",
"         <span class=\"medCalcFontIO\">",
"          Result:",
"         </span>",
"         <br/>",
"         &nbsp;",
"         <br/>",
