var script_f29_34_30241=[" /* <![CDATA[ */",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function Sokal_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.Sokal_form){",
"",
"",
"doCalc = true;",
"if (Age_param.value.indexOf(',') >= 0){ Age_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Age_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"Age = param_value;",
"if (Spleen_Below_Coastal_Margin_param.value.indexOf(',') >= 0){ Spleen_Below_Coastal_Margin_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Spleen_Below_Coastal_Margin_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Spleen_Below_Coastal_Margin_unit.options[Spleen_Below_Coastal_Margin_unit.selectedIndex].value.split('|');",
"Spleen_Below_Coastal_Margin = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Platelets_param.value.indexOf(',') >= 0){ Platelets_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Platelets_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Platelets_unit.options[Platelets_unit.selectedIndex].value.split('|');",
"Platelets = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Blast_Percent_param.value.indexOf(',') >= 0){ Blast_Percent_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Blast_Percent_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"Blast_Percent = param_value;",
"dp = 2;",
"Sokal_Score =  eTo(0.0116 * (Age - 43.4)+ 0.0345* (Spleen_Below_Coastal_Margin - 7.51) + 0.188 * (sq(Platelets / 700) - 0.563) + 0.0887 *(Blast_Percent - 2.1));",
"",
"if (doCalc) Sokal_Score_param.value = fixDP(Sokal_Score, dp);",
"",
"",
"",
"",
"if (doCalc){",
"",
"rrclr();",
"if (Sokal_Score < 0.8){ document.getElementById('rr1_1').bgColor = \"#cccccc\";",
"}",
"if ((Sokal_Score >= 0.8) && (Sokal_Score < 1.2)){ document.getElementById('rr1_2').bgColor = \"#cccccc\";",
"}",
"if (Sokal_Score >= 1.2){ document.getElementById('rr1_3').bgColor = \"#cccccc\";",
"}",
"",
"",
"",
"}",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.Sokal_form){",
"",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.Sokal_form){",
"",
"Sokal_Score_param.value = '';",
"",
"rrclr();",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"}",
"",
"",
"/* ]]> */"].join("\n");
