var title_f29_35_30267="Bullous drug eruptions";
var content_f29_35_30267=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F55836&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F55836&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Bullous drug eruptions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug eruption",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Common medications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Linear IgA bullous dermatosis",
"        </strong>",
"       </td>",
"       <td>",
"        Annular erythema and blisters (\"crown of jewels\").",
"       </td>",
"       <td>",
"        Vancomycin, penicillin, cephalosporins, ACE inhibitors, NSAIDs",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Pseudoporphyria",
"        </strong>",
"       </td>",
"       <td>",
"        Photodistributed blisters on dorsal hands, forearms, face. Similar clinical presentation as porphyria cutanea tarda (with negative porphyrin studies).",
"       </td>",
"       <td>",
"        NSAIDs, tetracyclines, furosemide",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Fixed drug eruption",
"        </strong>",
"       </td>",
"       <td>",
"        Dusky violaceous patch. Hemorrhagic vesicle or bulla. History of recurrences at same location with medication usage.",
"       </td>",
"       <td>",
"        Sulfonamides, NSAIDs, tetracyclines",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Stevens-Johnson syndrome, toxic epidermal necrolysis",
"        </strong>",
"       </td>",
"       <td>",
"        Widespread dusky erythema with epidermal detachment and flaccid blisters. Prominent mucosal involvement.",
"       </td>",
"       <td>",
"        Antibiotics, anticonvulsants, allopurinol, dapsone, NSAIDs",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     NSAIDs: Nonsteroidal antiinflammatory drugs",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
