var title_f29_38_30315="FIST violence screening";
var content_f29_38_30315=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F57574&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F57574&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    FISTS violence screening questions for adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Fights: How many fights have you been in in the past year? When was your last fight?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Teens who have been in more than one physical fight in the preceding 12 months are at increased risk of violence-related injury.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Injuries: Have you ever been injured in a fight? Have you ever injured anyone else?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Multiple",
"or serious previous injuries may indicate an increased risk of future",
"injury. Patients who have been injured may be more likely to carry",
"weapons.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Sexual violence: What happens when you and your boyfriend/girlfriend fight? Have you ever had sex against your will?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Teen-dating violence is associated with future domestic violence.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Threats: Have you ever been threatened?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Teenagers who have been threatened with a weapon are at future risk of weapons-related injury.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Self-defense: Have you ever carried a weapon to protect yourself?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Teenagers who arm themselves in self-defense are at increased risk for violence-related injury.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Alpert E, Sege R, Bradshaw Y. Academic Medicine 1997; 72 (suppl):S41.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
