"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        HIV myopathy",
"       </td>",
"       <td class=\"subtitle1\">",
"        NRTI myopathy",
"       </td>",
"       <td class=\"subtitle1\">",
"        Toxoplasma myopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Symptoms",
"       </td>",
"       <td>",
"        Myalgias, symmetric proximal muscle weakness",
"       </td>",
"       <td>",
"        Myalgias, symmetric proximal muscle weakness",
"       </td>",
"       <td>",
"        Diffuse muscle wasting, weakness, muscle tenderness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Muscle enzymes",
"       </td>",
"       <td>",
"        Markedly elevated (up to ten-fold)",
"       </td>",
"       <td>",
"        Markedly elevated (up to ten-fold)",
"       </td>",
"       <td>",
"        Usually increased",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Electromyography",
"       </td>",
"       <td>",
"        Myopathic changes",
"       </td>",
"       <td>",
"        Mild myopathic changes or normal",
"       </td>",
"       <td>",
"        Similar to polymyositis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Muscle biopsy",
"       </td>",
"       <td>",
"        Mononuclear cell infiltrate",
"       </td>",
"       <td>",
"        Mitochondrial myopathy, no or mild inflammation, cytochrome C oxidase deficiency",
"       </td>",
"       <td>",
"        Necrosis, variable inflammation, intracellular cysts containing T. gondii organisms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Treatment",
"       </td>",
"       <td>",
"        High dose corticosteroids",
"       </td>",
"       <td>",
"        Discontinue NRTI carnitine",
"       </td>",
"       <td>",
"        Pyrimethamine plus sulfadiazine",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
