var title_f29_43_30395="Breast cancer risk";
var content_f29_43_30395=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F79724&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F79724&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk of breast cancer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Percent of population",
"       </td>",
"       <td class=\"subtitle1\">",
"        Percent of all breast cancer cases",
"       </td>",
"       <td class=\"subtitle1\">",
"        Average risk of breast cancer to age 70",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Positive family history breast cancer*",
"       </td>",
"       <td>",
"        ~10",
"       </td>",
"       <td>",
"        15 to 20",
"       </td>",
"       <td>",
"        10 to 13 percent",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Positive BRCA1 or 2 mutation",
"       </td>",
"       <td>",
"        ~0.1",
"       </td>",
"       <td>",
"        5 to 6",
"       </td>",
"       <td>",
"        50 to 85 percent",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        General population without positive family history or BRCA mutation",
"       </td>",
"       <td>",
"        ~90",
"       </td>",
"       <td>",
"        80 to 85",
"       </td>",
"       <td>",
"        7",
"        <sup>",
"         &loz;",
"        </sup>",
"        percent",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Breast cancer in a first-degree relative.",
"     <br>",
"      <font class=\"bullet\">",
"       &bull;",
"      </font>",
"      Data from: Colditz G, et al. JAMA 1993; 270:338.",
"      <br>",
"       &Delta; This range represents the range of lifetime risk quoted by genetic specialists.",
"       <br>",
"        <font class=\"lozenge\">",
"         &loz;",
"        </font>",
"        Data from: the American Cancer Society, Cancer Statistics, 2010. (CA Cancer J Clin 2010; 60:277).",
"        <br/>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
