var title_f29_43_30396="Screening HBV high risk";
var content_f29_43_30396=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F55052&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F55052&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Groups at high risk for HBV infection who should be screened",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Individuals born in areas of high* or intermediate prevalence rates&bull; for HBV including immigrants and adopted children&Delta;&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asia: All countries",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Africa: All countries",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        South Pacific Islands: All countries",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Middle East (except Cyprus and Israel)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        European Mediterranean: Malta and Spain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The Arctic (indigenous populations of Alaska, Canada, and Greenland)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        South America: Ecuador, Guyana, Suriname, Venezuela, and Amazon regions of Bolivia, Brazil, Colombia, and Peru",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eastern Europe: All countries except Hungary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Caribbean: Antigua and Barbuda, Dominica, Granada, Haiti, Jamaica, St. Kitts and Nevis, St. Lucia, and Turks and Caicos",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Central America: Guatemala and Honduras",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Other groups recommended for screening",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        US born persons not vaccinated as infants whose parents were born in regions with high HBV endemicity (&ge;8 percent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Household and sexual contacts of HBsAg-positive persons&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persons who have ever injected drugs&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persons with multiple sexual partners or history of sexually transmitted disease&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Men who have sex with men&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inmates of correctional facilities&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Individuals with chronically elevated ALT or AST&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Individuals infected with HCV or HIV&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients undergoing renal dialysis&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        All pregnant women",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persons needing immunosuppressive therapy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * HBsAg prevalence 8 percent.",
"     <br>",
"      &bull; HBsAg prevalence 2-7 percent.",
"      <br>",
"       &Delta; If HBsAg-positive persons are found in the first generation, subsequent generations should be tested.",
"       <br>",
"        &loz; Those who are seronegative should receive hepatitis B vaccine.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Lok ASF, McMahon BJ. Chronic hepatitis B: Update 2009. Hepatology 2009; 50:661. Available online at file://publish.aasld.org/Pages/Default.aspx. Accessed September 8th 2009. Copyright &copy; 2009 American Association for the Study of Liver Diseases.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
