" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of locoregional recurrence of breast cancer after mastectomy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/45/30426/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/45/30426/contributors\">",
"     Ariel Hirsch, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/45/30426/contributors\">",
"     Lisa A Kachnic, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/45/30426/contributors\">",
