var title_f29_46_30445="Hypothermia RO";
var content_f29_46_30445=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F51697&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F51697&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rapid overview: Management of hypothermia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       General",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Definition: core temperature lower than 35&deg;C (95&deg;F)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Mild: 32-35&deg;C (90-95&deg;F)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Moderate: 28-32&deg;C (82-90&deg;F)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Severe: Below 28&deg;C (82&deg;F)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Use a low-reading core thermometer if hypothermia suspected; standard oral thermometers do not read below 34&deg;C (93&deg;F)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hypothermia secondary to other disease (infection, hypoglycemia, adrenal insufficiency, hypothyroidism, overdose, many others) must be considered",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Clinical aspects",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Physical examination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Vital signs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Mild hypothermia: tachypnea, tachycardia, hyperventilation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Moderate hypothermia: expect bradycardia; tachycardia suggests hypoglycemia, hypovolemia, or overdose",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Severe hypothermia: hypotension, cardiovascular collapse",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Neurological examination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Mild hypothermia: ataxia, dysarthria, impaired judgement; suspect CNS pathology if patient comatose",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Moderate hypothermia: CNS depression",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Severe hypothermia: areflexia, coma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Laboratory evaluation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Obtain coagulation studies, complete blood count, basic electrolytes, BUN and creatinine",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Clinical coagulopathy present despite normal measured coagulation times",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Increased hematocrit may reflect hemoconcentration",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; May see low bicarbonate, suggesting anion-gap acidosis; if so, obtain arterial blood gas",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Electrocardiogram",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Rhythm abnormalities (atrial fibrillation, sinus bradycardia) may be present",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Intervals (PR, QRS and QTc) may be prolonged",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Osborn J waves characteristic of hypothermia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Occurs at junction of QRS and ST segments, most prominent in V2-V5",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Distortion of the earliest phase of membrane depolarization",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Computer misinterprets as ischemic injury pattern",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Endotracheal intubation may be necessary in obtunded patients and those with bronchorrhea",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Treat hypotension with warmed crystalloid (42&deg;C) initially, dopamine if necessary",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Avoid rough movements and activity, which may induce ventricular fibrillation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Rewarming techniques are based on degree of hypothermia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Mild hypothermia: passive external rewarming",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Remove wet clothing, cover with blankets",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Moderate hypothermia: active external and internal rewarming",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Warmed humidified oxygen, forced air warming systems",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Beware of initial paradoxical drop in core temperature due to return of cold blood from extremities to core circulation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Rewarm trunk first to minimize risk of core temperature afterdrop",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Severe hypothermia: active internal rewarming (active core rewarming) and active external rewarming",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Warmed humidified oxygen, warmed IV fluids (42&deg;C)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Pleural and peritoneal irrigation with warm saline (40 to 42&deg;C)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Extracorporeal options: continuous venovenous rewarming, hemodialysis, continuous arteriovenous rewarming, and cardiopulmonary bypass",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Treatment of arrhythmias",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Arrhythmias may persist until patient rewarmed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Ignore atrial arrhythmias with slow ventricular response",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Ventricular fibrillation a common rhythm",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       May be precipitated by physical jostling",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Manage according to ACLS protocol",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Electrical defibrillation may be attempted but is rarely successful until core temperature is above 30&deg;C",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
