" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Massive hemoptysis: Initial management",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/47/30454/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/47/30454/contributors\">",
"     David H Ingbar, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/47/30454/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/47/30454/contributors\">",
"     Praveen N Mathur, MB, BS",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/47/30454/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/47/30454/contributors\">",
"     Geraldine Finlay, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?29/47/30454/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
