"    <div id=\"placeholder\">",
"     <div class=\"ieSpacer\">",
"     </div>",
"     <div id=\"basics-more\">",
"      <h4>",
"       <img src=\"file://www.uptodate.com/images/basics-more-title.png\"/>",
"      </h4>",
"      <dl>",
"       <dt class=\"basics\">",
"        The Basics",
"       </dt>",
"       <dd>",
"        <a href=\"UTD.htm?6/42/6818\">",
