" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Psychopharmacological treatment for adolescent depression",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?29/52/30538/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?29/52/30538/contributors\">",
