var title_f29_53_30555="Potential candidates for screening for pancreatic cancer";
var content_f29_53_30555=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F83695&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F83695&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Potential candidates for screening for pancreatic cancer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        An affected individual with PJS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        An affected individual with hereditary pancreatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Three or more first-, second-, or third-degree relatives with pancreatic cancer, with &ge;1 pancreatic cancer in a first-degree relative*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A known mutation carrier of a",
"        <em>",
"         BRCA1",
"        </em>",
"        ,",
"        <sup>",
"         &bull;",
"        </sup>",
"        <em>",
"         BRCA2",
"        </em>",
"        ,",
"        <em>",
"         p16",
"        </em>",
"        ,",
"        <em>",
"         MLH1",
"        </em>",
"        ,",
"        <sup>",
"         &bull;",
"        </sup>",
"        <em>",
"         MSH2",
"        </em>",
"        ,",
"        <sup>",
"         &bull;",
"        </sup>",
"        <em>",
"         MSH6",
"        </em>",
"        ,",
"        <sup>",
"         &bull;",
"        </sup>",
"        or",
"        <em>",
"         PMS2",
"        </em>",
"        <sup>",
"         &bull;",
"        </sup>",
"        mutation and &ge;1 first- or second- degree relative with pancreatic cancer",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PJS: Peutz-Jeghers Syndrome.",
"     <br/>",
"     * Screening may also be considered in individuals with two first-degree relatives with pancreatic cancer.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Data are more limited in these categories.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Grover S, Syngal S. Hereditary pancreatic cancer. Gastroenterology 2010; 139:1076. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
