var title_f29_53_30556="Management of subsolid pulmonary nodules detected at CT";
var content_f29_53_30556=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F87146&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F87146&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for the management of subsolid pulmonary nodules detected at CT: A statement from the Fleischner Society",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Nodule type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Management recommendations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Additional remarks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Solitary pure GGNs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &le;5 mm",
"       </td>",
"       <td>",
"        No CT follow-up required",
"       </td>",
"       <td>",
"        Obtain contiguous 1-mm-thick sections to confirm that nodule is truly a pure GGN",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &gt;5 mm",
"       </td>",
"       <td>",
"        Initial follow-up CT at&nbsp;three months to confirm persistence, then annual surveillance CT for a minimum of&nbsp;three years",
"       </td>",
"       <td>",
"        FDG PET is of limited value, potentially misleading, and therefore not recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Solitary part-solid nodules",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &nbsp;",
"       </td>",
"       <td>",
"        Initial follow-up CT at&nbsp;three months to confirm persistence. If persistent and solid component &lt;5 mm, then yearly surveillance CT for a minimum of&nbsp;three years. If persistent and solid component &ge;5 mm, then biopsy or surgical resection.",
"       </td>",
"       <td>",
"        Consider PET/CT for part-solid nodules &gt;10 mm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Multiple subsolid nodules",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pure GGNs &le;5 mm",
"       </td>",
"       <td>",
"        Obtain follow-up CT at&nbsp;two and&nbsp;four years",
"       </td>",
"       <td>",
"        Consider alternate causes for multiple GGNs &le;5 mm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pure GGNs &gt;5 mm without a dominant lesion(s)",
"       </td>",
"       <td>",
"        Initial follow-up CT at&nbsp;three months to confirm persistence and then annual surveillance CT for a minimum of&nbsp;three years",
"       </td>",
"       <td>",
"        FDG PET is of limited value, potentially misleading, and therefore not recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dominant nodule(s) with part-solid or solid component",
"       </td>",
"       <td>",
"        Initial follow-up CT at&nbsp;three months to confirm persistence. If persistent, biopsy or surgical resection is recommended, especially for lesions with &gt;5 mm solid component.",
"       </td>",
"       <td>",
"        Consider lung-sparing surgery for patients with dominant lesion(s) suspicious for lung cancer",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CT: computed tomography; FDG PET: 18-fluoro-2-deoxyglucose positron emission tomography; GGN: ground glass nodule.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Naidich DP, Bankier AA, MacMahon H, et al. Recommendations for the Management of Subsolid Pulmonary Nodules Detected at CT: A Statement from the Fleischner Society. Radiology 2013; 266:304-317. Copyright &copy; 2013 Radiological Society of North America.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
