var title_f29_56_30603="Hemicrania continua diagnostic criteria";
var content_f29_56_30603=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F62652&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F62652&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Hemicrania continua diagnostic criteria",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <strong>",
"         Description:",
"        </strong>",
"        Persistent strictly unilateral headache responsive to indomethacin.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Diagnostic criteria:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        A. Headache for more than three months fulfilling criteria B through D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        B. All of the following characteristics:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        1. Unilateral pain without side-shift",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        2. Daily and continuous, without pain-free periods",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        3. Moderate intensity, but with exacerbations of severe pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        C. At least one of the following autonomic features occurs during exacerbations and ipsilateral to the side of pain:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        1. Conjunctival injection and/or lacrimation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        2. Nasal congestion and/or rhinorrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        3. Ptosis and/or miosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        D. Complete response to therapeutic doses of indomethacin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        E. Not attributed to another disorder",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Headache classification subcommittee of the International Headache Society. The International Classification of Headache Disorders: 2nd edition. Cephalalgia 2004; 24 Suppl 1:9.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
