var title_f29_56_30604="Comparison between WHO criteria and the irRC";
var content_f29_56_30604=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F52907&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F52907&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison between WHO criteria and the Immune-related response criteria",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        WHO",
"       </td>",
"       <td class=\"subtitle1\">",
"        irRC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         New, measurable lesions",
"         <br/>",
"         (ie, &ge;5 x 5 mm)",
"        </strong>",
"       </td>",
"       <td>",
"        Always represent PD",
"       </td>",
"       <td>",
"        Incorporated into tumor burden",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         New, nonmeasurable lesions",
"         <br/>",
"         (ie, &lt;5 x 5 mm)",
"        </strong>",
"       </td>",
"       <td>",
"        Always represent PD",
"       </td>",
"       <td>",
"        Do not define progression (but preclude irCR)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Non-index lesions",
"        </strong>",
"       </td>",
"       <td>",
"        Changes contribute to defining best objective response of CR, PR, SD, and PD",
"       </td>",
"       <td>",
"        Contribute to defining irCR (complete disappearance required)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         CR",
"        </strong>",
"       </td>",
"       <td>",
"        Disappearance of all lesions in two consecutive observations not less than 4 wk apart",
"       </td>",
"       <td>",
"        Disappearance of all lesions in two consecutive observations not less than 4 wk apart",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         PR",
"        </strong>",
"       </td>",
"       <td>",
"        &ge;50 percent decrease in SPD of all index lesions compared with baseline in two observations at least 4 wk apart, in absence of new lesions or unequivocal progression of non-index lesions",
"       </td>",
"       <td>",
"        &ge;50 percent decrease in tumor burden compared with baseline in two observations at least 4 wk apart",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         SD",
"        </strong>",
"       </td>",
"       <td>",
"        50 percent decrease in SPD compared with baseline cannot be established nor 25 percent increase compared with nadir, in absence of new lesions or unequivocal progression of non-index lesions",
"       </td>",
"       <td>",
"        50 percent decrease in tumor burden compared with baseline cannot be established nor 25 percent increase compared with nadir",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         PD",
"        </strong>",
"       </td>",
"       <td>",
"        At least 25 percent increase in SPD compared with nadir and/or unequivocal progression of non-index lesions and/or appearance of new lesions (at any single time point)",
"       </td>",
"       <td>",
"        At least 25 percent increase in tumor burden compared with nadir (at any single time point) in two consecutive observations at least 4 wk apart",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     irRC: immune-related response criteria; PD: progressive disease; CR: complete response; PR: partial response; SD: stable disease; SPD: sum of the product of the two largest perpendicular diameters; irCR: immune-related complete response.",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted by permission from: the American Association for Cancer Research: Wolchok JD, Hoos A, O'Day S, et al. Guidelines for the evaluation of immune therapy activity in solid tumors: immune-related response criteria. Clin Cancer Res 2009 Dec 1; 15(23):7412-20. DOI: 10.1158/1078-0432.CCR-09-1624.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
