var script_f29_57_30609=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function CreatinineCl_CG_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.CreatinineCl_CG_form){",
"",
"",
"doCalc = true;",
"if (Sex_radio[0].checked){ Sex = 1; }",
"if (Sex_radio[1].checked){ Sex = 0.85; }",
"if (Age_param.value.indexOf(',') >= 0){ Age_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Age_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Age_unit.options[Age_unit.selectedIndex].value.split('|');",
"Age = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Serum_Creat_param.value.indexOf(',') >= 0){ Serum_Creat_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Serum_Creat_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Serum_Creat_unit.options[Serum_Creat_unit.selectedIndex].value.split('|');",
"Serum_Creat = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Weight_param.value.indexOf(',') >= 0){ Weight_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Weight_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Weight_unit.options[Weight_unit.selectedIndex].value.split('|');",
"Weight = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"Creat_Clear =  Sex * ((140 - Age) / (Serum_Creat)) * (Weight / 72);",
"",
"unit_parts = Creat_Clear_unit.options[Creat_Clear_unit.selectedIndex].value.split('|');",
"if (doCalc) Creat_Clear_param.value = fixDP((Creat_Clear - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.CreatinineCl_CG_form){",
"",
"if (Age_param.value && Age < 0) {",
"Age = 0;",
"Age_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Age is 0 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Age_param.value && Age > 120) {",
"Age_param.value = \"\";",
"clrResults();",
"Age = 0;",
"doCalc = false;",
"alert(\"The maximum value for Age is 120 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.CreatinineCl_CG_form){",
"",
"Creat_Clear_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
