"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F54965&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical presentation of bleomycin-induced lung injury",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Symptoms",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Dyspnea (75-90 percent)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Nonproductive cough (50-93 percent)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"1\" rowspan=\"2\">",
"            Chest discomfort",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Signs",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Fever",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Tachypnea",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Rales",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Cyanosis",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Laboratory",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Restrictive PFTs with a low diffusing capacity",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Widened alveolar-arterial gradient",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"1\" rowspan=\"2\">",
"            No eosinophilia (except in hypersensitivity-like reaction)",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
