var title_f29_59_30652="Virology of the common cold";
var content_f29_59_30652=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F56841&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F56841&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Epidemiologic and clinical features of viruses that cause the common cold in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Virus",
"       </td>",
"       <td class=\"subtitle1\">",
"        Proportion of cases",
"       </td>",
"       <td class=\"subtitle1\">",
"        Predominant months of circulation*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Other clinical syndromes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rhinoviruses",
"        <br/>",
"        (more than 100 serotypes)",
"       </td>",
"       <td>",
"        30 to 50 percent",
"       </td>",
"       <td>",
"        Present year round with a peak in September and a smaller peak in March and April",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Respiratory syncytial virus",
"       </td>",
"       <td>",
"        5 percent",
"       </td>",
"       <td>",
"        November to March",
"       </td>",
"       <td>",
"        Bronchiolitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Influenza viruses",
"       </td>",
"       <td>",
"        5 to 15 percent",
"       </td>",
"       <td>",
"        Winter months, with peak in February",
"       </td>",
"       <td>",
"        <p>",
"         Influenza",
"        </p>",
"        <p>",
"         Croup",
"        </p>",
"        Pneumonia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Parainfluenza viruses",
"       </td>",
"       <td>",
"        5 percent",
"       </td>",
"       <td>",
"        September to January with peak in October and November",
"       </td>",
"       <td>",
"        Croup",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adenoviruses",
"       </td>",
"       <td>",
"        &lt;5 percent",
"       </td>",
"       <td>",
"        September to May",
"       </td>",
"       <td>",
"        Pharyngoconjunctival fever",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Enteroviruses",
"        <br/>",
"        (echoviruses and coxsackieviruses)",
"       </td>",
"       <td>",
"        &lt;5 percent",
"       </td>",
"       <td>",
"        Present year round, with a peak during the summer",
"       </td>",
"       <td>",
"        <p>",
"         Aseptic meningitis (non-polio enteroviruses)",
"        </p>",
"        Herpangina (cocksackievirus type A)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coronaviruses",
"       </td>",
"       <td>",
"        10 to 15 percent",
"       </td>",
"       <td>",
"        November to February",
"       </td>",
"       <td>",
"        <p>",
"         Pneumonia",
"        </p>",
"        Croup",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Human metapneumovirus",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"       <td>",
"        Late winter and early spring",
"       </td>",
"       <td>",
"        <p>",
"         Pneumonia",
"        </p>",
"        Bronchitis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * In the northern hemisphere.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Heikkinen T, J&auml;rvinen A. The common cold. Lancet 2003; 361:51.",
"      </li>",
"      <li>",
"       Kirkpatrick GL. The common cold. Prim Care 1996; 23:657.",
"      </li>",
"      <li>",
"       Pappas DE, Hendley JO. The common cold. In: Principles and Practice of Pediatric Infectious Diseases, 3rd, Long SS, Pickering LK, Prober CG (Eds), Churchill Livingstone, Philadelphia 2008. p.203.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
