var title_f29_62_30701="Laxatives";
var content_f29_62_30701=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F59746&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F59746&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Laxatives for treatment of constipation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Laxative",
"       </td>",
"       <td class=\"subtitle1\">",
"        Usual adult dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Onset of action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Side effects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Bulk-forming laxatives*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Psyllium",
"       </td>",
"       <td>",
"        Up to 1 tablespoon (&cong;3.5 grams fiber) 3 times per day",
"       </td>",
"       <td>",
"        12 to 72 h",
"       </td>",
"       <td rowspan=\"4\">",
"        Impaction above strictures, fluid overload, gas and bloating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Methylcellulose",
"       </td>",
"       <td>",
"        Up to 1 tablespoon (&cong;2 grams fiber) or 4 caplets (500 mg fiber per caplet) 3 times per day",
"       </td>",
"       <td>",
"        12 to 72 h",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Polycarbophil",
"       </td>",
"       <td>",
"        2 to 4 tabs (500 mg fiber per tab) per day",
"       </td>",
"       <td>",
"        24 to 48 h",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Wheat dextrin",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        1 to 3 caplets (1 gram fiber per caplet) or 2 teaspoonsful (1.5 gram fiber per teaspoon) up to 3 times per daily",
"       </td>",
"       <td>",
"        24 to 48 h",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Surfactants (softeners)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Docusate sodium",
"       </td>",
"       <td>",
"        100 mg 2 times per day",
"       </td>",
"       <td>",
"        24 to 72 hours",
"       </td>",
"       <td rowspan=\"2\">",
"        Well tolerated. Use lower dose if administered with another laxative. Contact dermatitis reported.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Docusate calcium",
"       </td>",
"       <td>",
"        240 mg 1 time per day",
"       </td>",
"       <td>",
"        24 to 72 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Osmotic agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Polyethylene glycol (macrogol)",
"       </td>",
"       <td>",
"        8.5 to 34 grams in 240 mL (8 ounces) liquids",
"       </td>",
"       <td>",
"        1 to 4 days",
"       </td>",
"       <td>",
"        Nausea, bloating, cramping",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lactulose",
"       </td>",
"       <td>",
"        10 to 20 grams (15 to 30 mL) every other day. May increase up to 2 times per day.",
"       </td>",
"       <td>",
"        24 to 48 hours",
"       </td>",
"       <td>",
"        Abdominal bloating, flatulence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sorbitol",
"       </td>",
"       <td>",
"        30 grams (120 mL of 25 percent solution) 1 time per day",
"       </td>",
"       <td>",
"        24 to 48 hours",
"       </td>",
"       <td>",
"        Abdominal bloating, flatulence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Glycerin (glycerol)",
"       </td>",
"       <td>",
"        One suppository (2 or 3 grams) per rectum for 15 minutes 1 time per day",
"       </td>",
"       <td>",
"        15 to 60 minutes",
"       </td>",
"       <td>",
"        Rectal irritation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Magnesium sulfate",
"       </td>",
"       <td>",
"        One to two teaspoonsful (&cong;5 to 10 grams) dissolved in 240 mL (8 ounces) water 1 time per day",
"       </td>",
"       <td>",
"        0.5 to 3 hours",
"       </td>",
"       <td rowspan=\"2\">",
"        Watery stools and urgency. Caution in renal insufficiency (magnesium toxicity).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Magnesium citrate",
"       </td>",
"       <td>",
"        200 mL (11.6 grams) 1 time per day",
"       </td>",
"       <td>",
"        0.5 to 3 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Stimulant laxatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Bisacodyl",
"       </td>",
"       <td>",
"        10 to 30 mg as enteric coated tabs 1 time per day",
"       </td>",
"       <td>",
"        6 to 10 hours",
"       </td>",
"       <td>",
"        Gastric irritation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        10 mg suppository per rectum 1 time per day",
"       </td>",
"       <td>",
"        15 to 60 minutes",
"       </td>",
"       <td>",
"        Rectal irritation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Senna",
"       </td>",
"       <td>",
"        2 to 4 tabs (8.6 mg sennosides per tab) or 1 to 2 tabs (15 mg sennosides per tab) as a single daily dose or divided twice daily",
"       </td>",
"       <td>",
"        6 to 12 hours",
"       </td>",
"       <td>",
"        Melanosis coli",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lubiprostone",
"       </td>",
"       <td>",
"        24 micrograms 2 times per day",
"       </td>",
"       <td>",
"        24 to 48 hours",
"       </td>",
"       <td>",
"        Nausea, diarrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Linaclotide",
"       </td>",
"       <td>",
"        145 micrograms 1 time per day",
"       </td>",
"       <td>",
"        12 to 24 hours",
"       </td>",
"       <td>",
"        Diarrhea, bloating",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    All doses shown are for oral administration unless otherwise noted. Phosphate containing laxatives are not recommended. Mineral oil (enema and oral liquid) laxatives are not generally recommended except as enema following disimpaction (see text).",
"    <div class=\"footnotes\">",
"     * Initiate at one-half or less of dose shown and gradually increase as needed to minimize gas and bloating. Administer with 180 to 360 mL (6 to 12 ounces) water or fruit juice. Do not administer within one hour of other medications. Fiber content per dose may vary. Consult individual product label.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     US trade name Benefiber&reg;.",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Arnold Wald, MD. Additional data from: Lexicomp Online. Copyright &copy; 1978-2013 Lexicomp, Inc. All Rights Reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
