var title_f29_63_30716="Evaluation of cirrhosis";
var content_f29_63_30716=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F55647&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F55647&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of the patient with cirrhosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Disease",
"      </td>",
"      <td class=\"subtitle1\">",
"       Tests and findings",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"2\">",
"       Alcoholic liver disease",
"      </td>",
"      <td>",
"       History of alcohol abuse",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       AST/ALT &gt;2 with both being less than 500 IU/mL if alcoholic hepatitis is present",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"2\">",
"       Chronic hepatitis C",
"      </td>",
"      <td>",
"       ELISA assay for anti-HCV",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       PCR for HCV RNA if confirmatory test is necessary",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Primary biliary cirrhosis",
"      </td>",
"      <td>",
"       Antimitochondrial antibodies as an isolated finding",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"3\">",
"       Primary sclerosing cholangitis",
"      </td>",
"      <td>",
"       Strong association with inflammatory bowel disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Contrastcholangiography to establish the diagnosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Antinuclear and antismoothmuscle antibodies and ANCA; these are not diagnostic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Autoimmune hepatitis",
"      </td>",
"      <td>",
"       Hypergammaglobulinemia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Antinuclear and antismooth muscle antibodies and ANCA in type 1; anti-LKM-1 in type 2",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Chronic hepatitis B",
"      </td>",
"      <td>",
"       HBsAg and HBeAg and, in some cases, HBV DNA by hybridization or bDNA assay",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"3\">",
"       Hereditary hemochromatosis",
"      </td>",
"      <td>",
"       Family history of cirrhosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Transferrin saturation and plasma ferritin should be performed but may be elevated by liver disease itself",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Diagnosis established by liver biopsy and calculation of hepatic iron index or by genetic testing",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"3\">",
"       Wilson disease",
"      </td>",
"      <td>",
"       Family or personal history of cirrhosis at a young age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Serumceruloplasmin reduced in 95 percent of patients",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Liver biopsy showsincreased copper content which may also be seen in cholestatic liver diseases",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"2\">",
"       Alpha-1-antitrypsin deficiency",
"      </td>",
"      <td>",
"       Family or personal history of cirrhosis at a young age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Serum AAT; phenotyping if low or borderline values",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"2\">",
"       Nonalcoholic fatty liver disease",
"      </td>",
"      <td>",
"       History of diabetes mellitus or metabolic syndrome",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Diagnosis maybe suspected by abnormal liver biochemical tests and hepatic imagingshowing fatty infiltration and is confirmed by liver biopsy",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
