" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Cisplatin nephrotoxicity",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
